import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { movieActions } from "../redux/actions";
import { imageExists } from "../utils/index"
import { Card, Col, Row } from 'react-bootstrap'
import { Navibar, SideBar } from '../components'

function HomePage() {
  const { movies } = useSelector((s) => s.movie);
  console.log({ movies })
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(movieActions.getAll());
  }, [dispatch]);

  return (

    <div>
      <Navibar />
      <Row>
        {movies?.map((m) => {
          let imgUrl = 'https://d1csarkz8obe9u.cloudfront.net/posterpreviews/movie-poster-template-design-21a1c803fe4ff4b858de24f5c91ec57f_screen.jpg?ts=1574144362';
          if (imageExists("http://image.tmdb.org/t/p/w500" + m.poster_path)) {
            imgUrl = "http://image.tmdb.org/t/p/w500" + m.poster_path
          }
          return (
            <Col>
              <Card className="m-3" style={{ width: "18rem" }}>
                <Card.Img variant="top" src={imgUrl} />
                <Card.Body>
                  <Card.Title
                    style={{height: 50}}
                  >{m.title}</Card.Title>
                  <hr className="solid"></hr>
                  <Card.Text
                    style={{
                      height: 120,
                      overflow: "hidden",
                      overflowY: "auto",
                    }}
                  >
                    {m.description}
                  </Card.Text>
                  <hr className="solid"></hr>
                  <Card.Text>
                    Genre: {m.genre}
                  </Card.Text>
                  <hr className="solid"></hr>
                  <Card.Text>
                    Average vote: {m.avg_vote} from {m.votes} votes
                      </Card.Text>
                  <Card.Text
                    style={{
                      height: 120,
                      overflow: "hidden",
                      overflowY: "auto",
                    }}
                  >
                    Cast: {m.actors}
                  </Card.Text>
                  <hr className="solid"></hr>
                  <Card.Text> Director: {m.director}</Card.Text>
                </Card.Body>
              </Card>
            </Col>
          );
        })}
      </Row>
    </div>


  );
}

export { HomePage };
