import React from 'react'

const SideBar = ({moviesDefault, movies, setMovies,gen_ids}) => {

    // console.log(gen_ids)

    return (
        <div className="sidebar-content">
            <div className="form-content">
                <div>Most Popular</div>
                <div>New</div>
                <div>Highest Grossing</div>
                <div>Actors</div>
                <div>Genres</div>
            </div>

            <div className="total">
                <button 
                    onClick={() => setMovies(moviesDefault)}
                    className="total-btn">
                    Total
                </button>
            </div>

            <div className="category">
                {gen_ids.map(g => {
                    let newMovies =  moviesDefault.filter(m => m.genre_ids.includes(g.id) )
                    return (<button key={g.id} 
                                    onClick={() => setMovies(newMovies)}
                                    className="gen-btn">
                        <div>{g.count}</div>
                        <div>{g.name}</div>
                    </button>)
                })};
            </div>
        </div>
    )
}

export {SideBar}
