import React, { useEffect } from "react";
import { useDispatch, useSelector} from "react-redux";
import { movieActions } from "../redux/actions";
import {imageExists} from "../utils/index"
import {Navibar, SideBar} from '../components'

function HomePage() {
   const {movies} = useSelector((s) => s.movie);
   console.log({movies})
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(movieActions.getAll());
  }, [dispatch]);

  return(

    <div>
      <Navibar/>
      <SideBar/>
      <Row>
            {movies?.map((m) => {
              return (
                <Col>
                  <Card className="m-3" style={{ width: "18rem" }}>
                    <Card.Body>
                      <Card.Title
                        style={{
                          height: 50,
                        }}
                      >{m.title}</Card.Title>
                      <hr className="solid"></hr>
                      <Card.Text
                        style={{
                          height: 120,
                          overflow: "hidden",
                          overflowY: "auto",
                        }}
                      >
                        {m.description}
                      </Card.Text>
                      <hr className="solid"></hr>
                      <Card.Text>
                        Genre: {m.genre}
                      </Card.Text>
                      <hr className="solid"></hr>
                      <Card.Text>
                        Average vote: {m.avg_vote} from {m.votes} votes
                      </Card.Text>
                      <Card.Text
                        style={{
                          height: 120,
                          overflow: "hidden",
                          overflowY: "auto",
                        }}
                      >
                        Cast: {m.actors}
                      </Card.Text>
                      <hr className="solid"></hr>
                      <Card.Text> Director: {m.director}</Card.Text>
                    </Card.Body>
                  </Card>
                </Col>
              );
            })}
          </Row>
    </div>


  );
}

export { HomePage };


